import { Delete, Edit, ExpandLess, ExpandMore, Favorite, MoreVert, Share } from '@mui/icons-material'
import { Avatar, Card, CardActions, CardContent, CardHeader, CardMedia, Collapse, IconButton, List, ListItem, ListItemButton, ListItemText, Typography } from '@mui/material'
import React, { useState } from 'react'

const FeedsCard = ({ val }) => {
    const [colOpen,setColOpen] = useState(false);
    const [logger,setLogger] = useState({id:1,name:"Rahul"})

    const editComment = ()=>{

    }
    return (
        <Card sx={{ Width: 500, marginTop: "2rem", boxShadow: "rgba(0, 0, 0, 0.35) 0px 5px 15px" }}>
            <CardHeader
                avatar={
                    <Avatar >
                        {val.by.charAt(0)}
                    </Avatar>
                }
                action={
                    <IconButton aria-label="settings">
                        <MoreVert />
                    </IconButton>
                }
                title={val.by}
            />
            <CardMedia component="img" height="20%" image={val.postIMG} alt={val.postTitle} />
            <CardContent>
                <Typography variant="h6" color="text.primary">
                    {val.by}: {val.postTitle}

                </Typography>
            </CardContent>
            <CardActions disableSpacing>
                <IconButton aria-label="add to favorites">
                    <Favorite />
                </IconButton>
                <IconButton aria-label="share">
                    <Share />
                </IconButton>
                {colOpen ?  <ExpandLess aria-label="show more" onClick={()=>{setColOpen(!colOpen)}} expand={colOpen.toString()} sx={{cursor:"pointer"}} /> : <ExpandMore aria-label="show more" onClick={()=>{setColOpen(!colOpen)}} expand={colOpen.toString()} sx={{cursor:"pointer"}} />}
            </CardActions>
            <Collapse timeout="auto" in={colOpen} unmountOnExit >
                <CardContent>
                    <List>
                        {val.comments.map((val1,index) => {
                            return <ListItem key={index}>
                                <ListItemText primary={`${Object.keys(val1)[0]} : ${Object.values(val1)[0]} `} />
                                {Object.keys(val1)[0].toLowerCase() === logger.name.toLowerCase() && <ListItemButton sx={{display:"flex",justifyContent:"space-between"}}> <Edit onClick={editComment}/> <Delete onClick={(e)=> e.target.parentNode}/></ListItemButton>}
                            </ListItem>
                        })}
                       <input placeholder='comment' style={{width:"80%",fontSize:"1rem",padding:".3rem 1rem",borderRadius:"5px"}} autoFocus />
                    </List>
                </CardContent>
            </Collapse>
        </Card>
    )
}

export default FeedsCard
