import { Add } from '@mui/icons-material';
import { Fab, Modal, Tooltip, Typography, } from '@mui/material'
import { Box, styled } from '@mui/system';
import React from 'react'
import { useState } from 'react';
import { Context } from '../App'


const StyledModal = styled(Modal)({
  display: "flex", alignItems: "center", justifyContent: "space-between"
})
const UserBox = styled(Box)
const AddPost = () => {
  const { logger } = Context();
  const [open, setOpen] = useState(false)
  return (
    <>
      <Tooltip title="Create Post" onClick={() => setOpen(true)} sx={{ position: "fixed", top: "85%", left: { xs: '45%', md: 30 } }}>
        <Fab color="primary" aria-label="add" >
          <Add />
        </Fab>
      </Tooltip>

      <StyledModal open={open} onClose={() => setOpen(false)} >
        <Box width={600} height={200} bgcolor={'white'} borderRadius={5} p={4} >
          <Typography variant='h5' color="gray" textAlign={'center'} > Create Post</Typography>
          <UserBox></UserBox>
        </Box>
      </StyledModal>
    </>
  )
}

export default AddPost
