import React from 'react'
import { Context } from '../App'

const RightBar = () => {
  const { users } = Context();
  return (
    <Box flex={1.8} sx={{ display: { xs: "none", sm: "block" } }}>
      <Box sx={{ display: "flex", flexDirection: "column", alignItems: "flex-start",gap }}>
        <Typography variant='h5'> Friends </Typography>
        <AvatarGroup max={3}>
          {users.map((val) => <Avatar alt={val.fullName} key={val.id}> {val.name.charAt(0)} </Avatar>)}
        </AvatarGroup>
      </Box>
      <Box sx={{ display: "flex", flexDirection: "column", alignItems: "flex-start",p:"20px" }}>
        <Typography variant='h5'>Muted Friends </Typography>

      </Box>
    </Box>
  )
}

export default RightBar
