import React, { useContext } from 'react'
import { Link } from 'react-router-dom';
import { user } from '../App';


const Feed = () => {
  const detail = useContext(user);
  const file = ()=>{
    const fi = document.querySelector("#pic").files[0];
    let reader = new FileReader()

    console.log(reader.readAsDataURL(fi));
  }

  return (
    <>
      <div id='navFeed'>
        <Link to="/" style={{ fontSize: "5vh", textDecoration: "none", color: "#0a66c2", fontWeight: "700", marginTop: "2vh", marginRight: "30vh" }}>Connect <span style={{ backgroundColor: "#0a66c2", color: "white", padding: ".2vh .5vh", borderRadius: "1vh" }}>ing</span></Link>
        <div><i class="fa-solid fa-house"></i><div>Home</div></div>
        <div><i class="fa-solid fa-user-group"></i><div>Friends</div></div>
        <div><i class="fa-solid fa-comment-dots"></i> <div>Messages</div></div>
      </div>
      <div id='mainFeedContainer'>
        <div id='navFriends'>
          <h1 style={{ textAlign: "center", fontSize: "4vh", marginTop: "2vh", marginBottom: "5vh", backgroundColor: "#9f9", margin: "5vh", borderRadius: "5vh", position: "sticky", padding: "2vh 0" }}>Friends</h1>

          {detail.feed[0].map((val) => {
            return (
              <div style={{ display: "flex", justifyContent: "space-around", margin: "4vh 1vh", textAlign: "center", color: "dodgerblue", cursor: "pointer", backgroundColor: "#fff", boxShadow: "rgba(0, 0, 0, 0.25) 0px 54px 55px, rgba(0, 0, 0, 0.12) 0px -12px 30px, rgba(0, 0, 0, 0.12) 0px 4px 6px, rgba(0, 0, 0, 0.17) 0px 12px 13px, rgba(0, 0, 0, 0.09) 0px -3px 5px", alignItems: "center", padding: "1vh 3vh", borderRadius: "5vh" }}><h1 style={{ display: "inline" }}>{val.friendName}</h1><i class="fa-solid fa-circle-dot" ></i></div>
            )
          })}

        </div>
        <div id='navFeedDisplay'>
          <div id='posting'>
            <input placeholder="What's on Your mind?" type="text" style={{fontSize:"3vh",width:"100%"}}/>
            <input type="file"  style={{fontSize:"3vh",width:"100%"}} id="pic" />
            <button onClick={file}>Click Here</button>


          </div>

        </div>
        <div id='navFeedAds'>
          <h3 style={{ textAlign: "center", fontSize: "5vh" }}>Ads</h3>
        </div>
      </div>
    </>
  )
}

export default Feed;
