import { Comment, Delete, Edit, ExpandLess, ExpandMore, Favorite, MoreVert, Share } from '@mui/icons-material'
import { Avatar, Card, CardActions, CardContent, CardHeader, CardMedia, Collapse, IconButton, InputAdornment, InputBase, List, ListItem, ListItemButton, ListItemText, TextField, Typography } from '@mui/material'
import React, { useState } from 'react'
import { Context } from '../App'

const FeedsCard = ({ val }) => {
    const { details, setDetails } = Context()
    const [colOpen, setColOpen] = useState(false);
    const [logger, setLogger] = useState({ id: 1, name: "Rahul" });
    const [comment, setComment] = useState("");
    // const [count,setCount] = useState(0);

    const editComment = (e) => {
        details.forEach((val2) => {
            if (val2.id === val.id) {
                val.comments.splice(e.target.id, 1)
                setComment(Object.values(val.comments[e.target.id]))
            }
        })

    }
    const delComment = (e) => {
        details.forEach((val2) => {
            if (val2.id === val.id) {
                val.comments.splice(e.target.id, 1)
            }
        })
    }
    const commented = (e) => {
        if (e.key === 'Enter') {
            details.forEach((val2) => {
                if (val2.id === val.id) {
                    const obj = {}
                    obj[logger.name] = comment;
                    val.comments.push(obj);
                    setComment("");
                }
            })
        }
    }

    return (
        <Card sx={{ Width: 500, marginTop: "2rem", boxShadow: "rgba(0, 0, 0, 0.35) 0px 5px 15px" }}>
            <CardHeader
                avatar={
                    <Avatar >
                        {val.by.charAt(0)}
                    </Avatar>
                }
                action={
                    <IconButton aria-label="settings">
                        <MoreVert />
                    </IconButton>
                }
                title={val.by}
            />
            <CardMedia component="img" height="20%" image={val.postIMG} alt={val.postTitle} />
            <CardContent>
                <Typography variant="h6" color="text.primary">
                    {val.by}: {val.postTitle}

                </Typography>
            </CardContent>
            <CardActions disableSpacing>
                <IconButton aria-label="add to favorites">
                    <Favorite />
                </IconButton>
                <IconButton aria-label="share">
                    <Share />
                </IconButton>
                {colOpen ? <ExpandLess aria-label="show more" onClick={() => { setColOpen(!colOpen) }} expand={colOpen.toString()} sx={{ cursor: "pointer" }} /> : <ExpandMore aria-label="show more" onClick={() => { setColOpen(!colOpen) }} expand={colOpen.toString()} sx={{ cursor: "pointer" }} />}
            </CardActions>
            <Collapse timeout="auto" in={colOpen} unmountOnExit >
                <CardContent>
                    <List>
                        {val.comments.map((val1, index) => {
                            return <ListItem key={index}>
                                <ListItemText primary={`${Object.keys(val1)[0]} : ${Object.values(val1)[0]} `} />
                                {Object.keys(val1)[0].toLowerCase() === logger.name.toLowerCase() && <ListItemButton sx={{ display: "flex", justifyContent: "space-between" }}> <Edit onClick={editComment} id={index} /> <Delete onClick={delComment} id={index} /></ListItemButton>}
                            </ListItem>
                        })}
                        <TextField id="outlined-basic" label="Comment" variant="outlined" sx={{ width: "90%", fontSize: "1rem", borderRadius: "5px", marginLeft: "1rem" }} autoFocus onChange={(e) => { setComment(e.target.value) }} value={comment} onKeyPress={commented} />
                    </List>
                </CardContent>
            </Collapse>
        </Card>
    )
}

export default FeedsCard
